for(var cartLinks=document.querySelectorAll(".buy"),cartPopup=document.querySelector(".modal-cart"),cartClose=cartPopup.querySelector(".modal-close"),continueShop=cartPopup.querySelector(".modal-cart-continue"),j=0;j<cartLinks.length;j++)cartLinks[j].addEventListener("click",function(e){e.preventDefault(),cartPopup.classList.add("modal-show")});cartClose.addEventListener("click",function(e){e.preventDefault(),cartPopup.classList.remove("modal-show")}),continueShop.addEventListener("click",function(e){e.preventDefault(),cartPopup.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),cartPopup.classList.contains("modal-show")&&cartPopup.classList.remove("modal-show"))});